<?phpclass DashboardController extends Controller{	public function actionIndex()	{		$this->render('index', array('tab'=>'me'));	}		public function actionEveryone()	{		$this->render('index', array('tab'=>'everyone'));	}		public function actionCategories()	{		$this->render('index', array('tab'=>'categories'));	}		public function actionHistory()	{		$this->render('index', array('tab'=>'history'));	}			// view a stored page, just like in front end 	public function actionViewPage($url_keyword)	{		$page = Page::model()->findByAttributes(array('url_keyword'=>$url_keyword));		if ($page == null)			throw new CHttpException(404,'The requested page does not exist.');					$this->render('viewPage',array(			'model'=>$page,		));	}}