<?phpclass ToolsController extends Controller{	// public $layout = 'column2';		function actionIndex()	{		$this->render('index');	}		function actionXmlSitemap()	{		$posts = Post::model()->findAll(array(			'condition'=>'status = '.Post::STATUS_PUBLISHED,			'order'=>'create_time DESC',		));				$categories = Category::model()->findAll(array(			'condition'=>'status = '.Category::STATUS_PUBLISHED,			'order'=>'parent_id, view_order',		));				$this->renderPartial('xmlSitemap', array(			'posts'=>$posts,			'categories'=>$categories,		));	}}