	function is_file_missing($file) { global $existing_files_global; return !in_array($file, $existing_files_global); }
	$orphan_files = array_filter($existing_files, 'is_file_orphan');
	$missing_files = array_filter(array_keys($files_used_by_posts), 'is_file_missing');
	
	$used_space = 0;
	$orphan_space = 0;
	$p = dirname(Yii::app()->basePath);
	foreach ($used_files as $used_file)
	{
		// files with a space in them are not recognized
		if (!is_file($p . $used_file))
			continue;
		$used_space += filesize($p . $used_file);
	}
	foreach ($orphan_files as $orphan_file)
		$orphan_space += filesize($p . $orphan_file);
	
	
	echo '<h2>Στατιστικά αρχεία</h2>';
	echo '<ul>';
	echo '<li><b>' . count($existing_files) . '</b> υπάρχοντα αρχεία στο /uploads ('.round($used_space / (1024*1024)).' MB)';
	echo '<li><b>' . count($files_used_by_posts) . '</b> αρχεία χρησιμοποιούνται από τις αναρτήσεις';
	echo '<li><b>' . count($files_used_by_old_volax_tinos_gr) . '</b> αρχεία χρησιμοποιούνται από τις παλιές σελίδες του volax-tinos.gr';
	echo '<li><b>' . count($orphan_files) . '</b> αρχεία που υπάρχουν, αλλά δεν χρησιμοποιούνται ('.round($orphan_space / (1024*1024)).' MB)';
	echo '<li><b>' . count($missing_files) . '</b> αρχεία που χρησιμοποιούνται, αλλά δεν βρέθηκαν';
	echo '</ul>' . "\r\n\r\n";
	
	
	echo '<h2>Αρχεία που υπάρχουν, αλλά δεν χρησιμοποιούνται</h2>';
	echo '<textarea cols="70" rows="10" style="width:100%;">' . implode("\r\n", $orphan_files) . "\r\n" . '</textarea>';
	sort($orphan_files);
	echo '<p>&nbsp;</p>';
	
	
	echo '<h2>Αρχεία που χρησιμοποιούνται, αλλά δεν βρέθηκαν</h2>';
	sort($missing_files);
	foreach ($missing_files as $missing_file)
	{
		echo CHtml::encode($missing_file);
		$post_ids = $files_used_by_posts[$missing_file];
		foreach ($post_ids as $post_id)
			echo ' ' . CHtml::link('#' . $post_id, array('/admin/posts/update', 'id'=>$post_id));
		echo '<br />';
	}
	echo '<p>&nbsp;</p>';
	
	
	echo '<h2>Αρχεία που χρησιμοποιούνται σε αναρτήσεις</h2>';
	$used_paths = array_keys($files_used_by_posts);
	sort($used_paths);
	foreach ($used_paths as $used_path)
	{
		echo CHtml::encode($used_path) . ' &nbsp; ';
		$post_ids = $files_used_by_posts[$used_path];
		foreach ($post_ids as $post_id)
			echo ' ' . CHtml::link('#' . $post_id, array('/admin/posts/update', 'id'=>$post_id));
		echo '<br />';
	}
	echo '<p>&nbsp;</p>';
	
	
	
?>


