<?php	// width based on desired width. to fit in a 900px space.	$w = 0;	$m = 0;	if ($data->desired_width == 1) {		$w = 240;		$m = 60;	} 	else if ($data->desired_width == 3) {		$w = 720 + 60 + 60;		$m = 60;	}	else {		$w = 480 + 60;		$m = 60;	}?><div class="post" style="width:<?php echo $w; ?>px; margin-right: <?php echo ($m-1); ?>px;float:left;">	<div class="title">		<?php echo CHtml::link(CHtml::encode($data->title), $data->url); ?>	</div>		<?php		$imgHtml = $data->getImageHtml($w);		if ($imgHtml !== false)			echo Chtml::tag('div', array('class'=>'image'), $imgHtml);	?>			<div class="content">		<?php echo $data->getContentHtmlUptoMore(); ?>	</div>			<?php $this->renderPartial('/post/_postInfoMinimal',array(		'post'=>$data,	)); ?></div>