<div class="post medium">	<?php		echo CHtml::tag('div', array('class'=>'title'), CHtml::link(CHtml::encode($data->title), $data->url));		if ($data->image_filename != '')			echo CHtml::image($data->image_filename);			echo CHtml::tag('div', array('class'=>'content'), $data->getContentHtmlUptoMore());			$this->renderPartial('/post/_postInfoFull',array(			'post'=>$data,		));	?></div>