<?php	$widthClass = ($data->desired_width == 1) ? 'narrow' : ($data->desired_width == 3 ? 'wide' : 'medium');	$extraClass = ($data->image_filename == '') ? ' post-with-no-image' : '';?><div class="post <?php echo $widthClass; echo $extraClass; ?>">		<?php		if ($data->masthead != '')			echo CHtml::tag('div', array('class'=>'masthead'), CHtml::encode($data->masthead));		echo CHtml::tag('div', array('class'=>'title'), CHtml::link(CHtml::encode($data->title), $data->url));		if ($data->image_filename != '')			echo CHtml::image($data->image_filename);		if ($data->prologue != '')			echo CHtml::tag('div', array('class'=>'prologue'), CHtml::encode($data->prologue));		echo CHtml::tag('div', array('class'=>'content'), $data->getContentHtmlUptoMore());				$this->renderPartial('/post/_postInfoMinimal',array(			'post'=>$data,		)); 	?></div>