<div class="post" style="width:<?php echo ($data->render_narrow == 1) ? 31 : 64; ?>%; margin-right: 2%;">	<div class="title">		<?php echo CHtml::link(CHtml::encode($data->title), $data->url); ?>	</div>			<?php		// shouldn't we use image2 for this?		if ($data->image_filename != '')		{			echo '<div class="image">';			$fn = $data->image_filename;			if (substr($fn, 0, 8) == '/volax4/')				$fn = substr($fn, 8);			$img = $this->createUrl('/images/show', array('src'=>$fn, 'width'=>400, 'height'=>300));			// echo $img;			//echo CHTml::image($img);			echo CHTml::image($data->image_filename);			echo '</div>';		}	?>			<div class="content">		<?php echo $data->getContentHtmlUptoMore(); ?>	</div>			<?php $this->renderPartial('/post/_postInfo',array(		'post'=>$data,	)); ?></div>