<div class="post narrow">	<?php		echo CHtml::tag('div', array('class'=>'title'), CHtml::link(CHtml::encode($data->title), $data->url));		$imgHtml = $data->getImageHtml();		if ($imgHtml !== false)			echo Chtml::tag('div', array('class'=>'image'), $imgHtml);		echo CHtml::tag('div', array('class'=>'content'), $data->getContentHtmlUptoMore());			$this->renderPartial('/post/_postInfoMinimal',array(			'post'=>$data,		));	?></div>