<div class="post">	<?php		if ($data->masthead != '')		{			echo '<div class="masthead">';			echo CHtml::encode($data->masthead);			echo '</div>' . "\r\n";		}	?>			<div class="title">		<?php echo CHtml::link(CHtml::encode($data->title), $data->url); ?>	</div>			<?php		if ($data->prologue != '')		{			echo '<div class="prologue">';			echo CHtml::encode($data->prologue);			echo '</div>' . "\r\n";		}	?>			<?php		if ($data->image_filename != '')		{			echo '<div class="image">';			$fn = $data->image_filename;			if (substr($fn, 0, 8) == '/volax4/')				$fn = substr($fn, 8);			$img = $this->createUrl('/images/show', array('src'=>$fn, 'width'=>400, 'height'=>300));			// echo $img;			//echo CHTml::image($img);			echo CHTml::image($data->image_filename);			echo '</div>';		}	?>			<div class="content">		<?php echo $data->getContentHtmlIncludingMore(); ?>	</div>			<?php $this->renderPartial('/post/_postInfo',array(		'post'=>$data,	)); ?></div>