<div class="post">	<?php		if ($data->masthead != '')		{			echo '<div class="masthead">';			echo CHtml::encode($data->masthead);			echo '</div>' . "\r\n";		}	?>			<div class="title">		<?php echo CHtml::link(CHtml::encode($data->title), $data->url); ?>	</div>			<?php		if ($data->prologue != '')		{			echo '<div class="prologue">';			echo CHtml::encode($data->prologue);			echo '</div>' . "\r\n";		}	?>			<?php		$imgHtml = $data->getImageHtml($w);		if ($imgHtml !== false)			echo Chtml::tag('div', array('class'=>'image'), $imgHtml);	?>			<div class="content">		<?php echo $data->getContentHtmlIncludingMore(); ?>	</div>			<?php $this->renderPartial('/post/_postInfoFull',array(		'post'=>$data,	)); ?></div>