<div class="post wide">	<?php		if ($data->masthead != '')			echo CHtml::tag('div', array('class'=>'masthead'), CHtml::encode($data->masthead));		echo CHtml::tag('div', array('class'=>'title'), CHtml::link(CHtml::encode($data->title), $data->url));		if ($data->prologue != '')			echo CHtml::tag('div', array('class'=>'prologue'), CHtml::encode($data->prologue));		if ($data->image_filename != '')			echo CHtml::image($data->image_filename);		echo CHtml::tag('div', array('class'=>'content'), $data->getContentHtmlIncludingMore());		$this->renderPartial('/post/_postInfoFull',array(			'post'=>$data,		));	?></div>