<div class="post">	<?php		if ($data->masthead != '')		{			echo '<div class="masthead">';			echo CHtml::encode($data->masthead);			echo '</div>' . "\r\n";		}	?>			<div class="title">		<?php echo CHtml::link(CHtml::encode($data->title), $data->url); ?>	</div>			<?php		if ($data->prologue != '')		{			echo '<div class="prologue">';			echo CHtml::encode($data->prologue);			echo '</div>' . "\r\n";		}	?>			<?php		// couldn't we use image2 for this?		if ($data->image_filename != '')		{			echo '<div class="image">';			$fn = $data->image_filename;			$w = 500;						// must find a way to automate this...			if (substr($fn, 0, 8) == '/volax4/')				$fn = substr($fn, 8);			if (substr($fn, 0, 4) == '/v4/')				$fn = substr($fn, 4);							// width will be dependent on post layout...			$url = Yii::app()->createUrl('/images/show', array(				'src'=>$fn,				'width'=>$w,			));			echo CHTml::image($url);			// echo CHTml::image($data->image_filename);			echo '</div>';		}	?>			<div class="content">		<?php echo $data->getContentHtmlIncludingMore(); ?>	</div>			<?php $this->renderPartial('/post/_postInfo',array(		'post'=>$data,	)); ?></div>