<div class="post wide">	<?php		if ($data->masthead != '')			echo CHtml::tag('div', array('class'=>'masthead'), CHtml::encode($data->masthead));		echo CHtml::tag('div', array('class'=>'title'), CHtml::link(CHtml::encode($data->title), $data->url));		if ($data->prologue != '')			echo CHtml::tag('div', array('class'=>'prologue'), CHtml::encode($data->prologue));		$imgHtml = $data->getImageHtml();		if ($imgHtml !== false)			echo Chtml::tag('div', array('class'=>'image'), $imgHtml);		$content = $data->getContentHtmlIncludingMore();		$content = $this->widget('application.components.ContentProcessor', array('content'=>$content), true);		echo CHtml::tag('div', array('class'=>'content'), $content);				$this->renderPartial('/post/_postInfoFull',array(			'post'=>$data,		));	?></div>