<?php $widthClass = ($data->desired_width == 1) ? 'narrow' : ($data->desired_width == 3 ? 'wide' : 'medium'); ?><div class="post <?php echo $widthClass; ?>">	<?php		if ($data->masthead != '')			echo CHtml::tag('div', array('class'=>'masthead'), CHtml::encode($data->masthead)) . "\r\n";		echo CHtml::tag('div', array('class'=>'title'), CHtml::link(CHtml::encode($data->title), $data->url)) . "\r\n";		$content = $data->getContentHtmlUptoMore();		$content = Yii::app()->contentProcessor->process($content);		echo CHtml::tag('div', array('class'=>'content'), $content) . "\r\n";			?>		<div class="post-info">		<?php 			echo CHtml::link(CHtml::encode($data->author == null ? '(άγνωστος)' : $data->author->fullname), array('/user/view', 'id'=>$data->author_id, 'name'=>$data->author->username));			echo ', ';			echo $data->getFriendlyCreateTime(); 						if ($data->category != null) {				echo ', <b>' . CHtml::link($data->category->title, $data->category->getUrl()) . '</b>';			}			if (count($data->tagLinks) > 0) {				echo ', tags: ' . implode(', ', $data->tagLinks) . '';			}						if ($data->commentCount > 0) {				$caption = $data->commentCount == 1 ? 'σχόλιο' : 'σχόλια';				echo ', ' . CHtml::link($data->commentCount . ' ' . $caption, $data->url.'#comments');			}		?>	</div></div>