	<div class="author">		Από τον χρήστη <?php echo $post->author->username . ' στις ' . $post->getFriendlyCreateTime(); ?>	</div>	<div class="nav">		<?php			$newline = false;			$comma = false;						if ($post->category != null) {				echo 'Στήλη: <b>' . CHtml::link($post->category->title, $post->category->getUrl()) . '</b>';				$comma = true;				$newline = true;			}			if (count($post->tagLinks) > 0) {				if ($comma)					echo ', ';								echo 'Tags: <b>' . implode(', ', $post->tagLinks) . '</b>';				$newline = true;			}							if ($newline)				echo '<br />';						if ($post->commentCount > 0) {				echo CHtml::link("{$post->commentCount} σχόλια", $post->url.'#comments');				echo ' | ';			}						echo $post->getFriendlyCreateTime();		?>	</div>