<div class="post-entry">	<?php		$link = CHtml::link(CHtml::encode($data->title), $data->url);		echo CHtml::tag('h3', array('class'=>'title'), $link);				echo CHtml::tag('div', array('class'=>'excerpt'), $data->excerpt);				$info = '';		$info .= $data->getFriendlyCreateTime(); 		$info .= ', ';		$info .= CHtml::link(CHtml::encode($data->author == null ? '(άγνωστος)' : $data->author->fullname), array('/user/view', 'id'=>$data->author_id));		if ($data->category != null)			$info .= ', ' . CHtml::link($data->category->title, $data->category->getUrl()) . '';		echo CHtml::tag('div', array('class'=>'info'), $info);	?></div>